
public class Ejercicio4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int N = 1;
		
		System.out.println("valor inicial de N = " + N);
		
		N = N + 77;
		
		System.out.println("valor una vez incrementado N en 77 = " +N);
		
		N = N - 3;
		
		System.out.println("valor una vez decrementado N en 3 = " +N);
		
		N = N * 2;
		
		System.out.println("valor una vez duplicado el valor de N =" +N);

	}

}
